var button=document.querySelector(".search-button");var popup=document.querySelector(".form-wrap");var date=popup.querySelector("[name=arrival-date]");var search=popup.querySelector(".search-form");var date2=popup.querySelector("[name=depature-date]");var amount1=popup.querySelector("[name=adults]");var amount2=popup.querySelector("[name=children]");popup.classList.add("modal-close");button.addEventListener("click",function(a){a.preventDefault();if(popup.classList.contains("modal-show")){popup.classList.remove("modal-show")}else{popup.classList.add("modal-show")}});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(popup.classList.contains("modal-show")){popup.classList.remove("modal-show")}}});search.addEventListener("submit",function(a){a.preventDefault();if(!date.value||!date2.value||!amount1.value||!amount2.value){popup.classList.remove("modal-error");popup.offsetWidth=popup.offsetWidth;popup.classList.add("modal-error")}else{search.submit()}});